mapper.select:
select U.NICKNAME, U.LOCATION, U.PHOTO, U.USER_NO as userNo, U.GRADE, U.POINT, U.PROGRAM_NAME as programName
from users u, groupmember m
where m.user_no = u.user_no and m.group_no = #{groupNo}

select g.group_no, g.groupcomm_no, g.user_no, u.grade, u.photo, u.nickname, g.groupcomm_content,  to_char(g.groupcomm_date, 'YY.MM.DD. AMHH12:MI:SS','NLS_DATE_LANGUAGE=KOREAN') groupcomm_date
from groupcomment g , users u
where g.group_no = #{groupNo}

select count(user_no) count
from grouparticle
where user_no = #{userNo} and group_no = #{groupNo}

select count(user_no) count
from groupmember
where user_no = #{userNo} and group_no = #{groupNo}
