mapper.select:
select U.NICKNAME, U.LOCATION, U.PHOTO, U.USER_NO as userNo, U.GRADE, U.POINT, U.PROGRAM_NAME as programName
from users u, groupmember m
where m.user_no = u.user_no and m.group_no = #{groupNo}

select count(groupcomm_no)
from groupcomment
where group_no=#{groupNo}

select count(user_no) count
from grouparticle
where user_no = #{userNo} and group_no = #{groupNo}

select count(user_no) count
from groupmember
where user_no = #{userNo} and group_no = #{groupNo}
